from django.contrib import adminfrom .models import *class ArticleAdmin(admin.ModelAdmin):    list_display = ('author','title','category_to_str','date','status','is_top')    list_filter = ('date', 'status', 'author')    search_fields = ('title','author')    prepopulated_fields = {'slug': ('title',)}    ordering = ['status', 'date']admin.site.register(Article, ArticleAdmin)class CategoryAdmin(admin.ModelAdmin):    list_display = ('title', 'slug', 'status')    list_filter = (['status'])    search_fields = ('title', 'slug')    prepopulated_fields = {'slug': ('title',)}admin.site.register(Category, CategoryAdmin)