from django.urls import pathfrom . import viewsapp_name = 'blog'urlpatterns = [    path("", views.blog, name='blog-page'),    path("detail/<slug:slug>", views.blog_detail, name='blog-detail'),    path('ajax/load-article/', views.article_filter, name='ajax_load_article'), # AJAX    path("manager/", views.blog_manager, name='blog-manager-page'),    path("update/<int:pk>", views.update_article, name='update-article'),    path("delete/<int:pk>", views.delete_article, name='delete-article'),    path("create/", views.create_article, name='create-article'),    path("create/category/", views.create_category, name='create-category')]